sudo awk ' BEGIN {print("MIME type (lowercased)\t\t\tExtensions\n========================================================")} {print} ($1 ~ /text\/*/) {t1 += 1} ($1 ~ /text\/*/)&&(length($2) != 0)&&(length($3) != 0)&&(length($4) == 0) {t2 += 1} END {print("========================================================\nMIME text type amount: ", t1, "\nMIME text type amount with two extensions: ", t2)}' mime.txt > text2.txt